  info: {
    border: "border-l-4 border-l-chart-3",
    icon: Info,
    iconColor: "text-chart-3",
    badge: "bg-chart-3/10",
    badgeText: "text-chart-3",
  },
};

export default function AlertCard({
  id,
  title,
  boxId,
  boxName,
  severity,
  timestamp,
  resolved = false,
  onClick,
}: AlertCardProps) {
  const style = severityStyles[severity];
