import { motion } from "framer-motion";
import { Box, AlertTriangle, Thermometer, Droplets, Clock } from "lucide-react";
import { Card } from "@/components/ui/card";

interface SummaryCardProps {
  type: "boxes" | "alerts" | "summary";
