import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0" +
  " hover-elevate active-elevate-2",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground border border-primary-border",
        destructive:
          "bg-destructive text-destructive-foreground border border-destructive-border",
        outline:
          // Shows the background color of whatever card / sidebar / accent background it is inside of.
          // Inherits the current text color.
          " border [border-color:var(--button-outline)]  shadow-xs active:shadow-none ",
        secondary: "border bg-secondary text-secondary-foreground border border-secondary-border ",
        // Add a transparent border so that when someone toggles a border on later, it doesn't shift layout/size.
        ghost: "border border-transparent",
      },
      // Heights are set as "min" heights, because sometimes Ai will place large amount of content
      // inside buttons. With a min-height they will look appropriate with small amounts of content,
      // but will expand to fit large amounts of content.
      size: {
        default: "min-h-9 px-4 py-2",
        sm: "min-h-8 rounded-md px-3 text-xs",
        lg: "min-h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

client/src/pages/home.tsx.content:
import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useAuth } from "@/context/AuthContext";
import DashboardHeader from "@/components/DashboardHeader";
import SummaryCard from "@/components/SummaryCard";
import BoxCard from "@/components/BoxCard";
import BoxDetails from "@/components/BoxDetails";
import BottomNavigation, { type TabId } from "@/components/BottomNavigation";
import AlertCard from "@/components/AlertCard";
import AlertDetails from "@/components/AlertDetails";
import ProfileScreen from "@/components/ProfileScreen";
import SettingsScreen from "@/components/SettingsScreen";
import LanguageModal from "@/components/LanguageModal";
import { Card } from "@/components/ui/card";
import { useTheme } from "@/context/ThemeContext";
import { useToast } from "@/hooks/use-toast";
import { useTranslation } from "@/hooks/useTranslation";
import { Shield, Leaf, Info } from "lucide-react";
import type { BoxStatus } from "@/components/BoxCard";
import type { AlertSeverity } from "@/components/AlertCard";

export type AlertType = "high_temp" | "low_humidity" | "sensor_disconnected" | "box_movement";

export interface BoxData {
  id: string;
  name: string;
  status: BoxStatus;
  temperature: number;
  humidity: number;
  lastUpdated: string;
  peltierOn: boolean;
  fixAttempts: number;
}

export interface AlertData {
  id: string;
  alertType: AlertType;
  title: string;
  message: string;
  boxId: string;
  boxName: string;
  severity: AlertSeverity;
  timestamp: string;
  resolved: boolean;
  fixAttempts: number;
}

const initialBoxes: BoxData[] = [
  { 
    id: "BOX123", 
    name: "Storage Box 1", 
    status: "warning", 
    temperature: 29, 
    humidity: 52, 
    lastUpdated: "2 mins ago", 
    peltierOn: false,
    fixAttempts: 0
  },
  { 
    id: "BOX124", 
    name: "Storage Box 2", 
    status: "online", 
    temperature: 24, 
    humidity: 65, 
    lastUpdated: "1 min ago", 
    peltierOn: true,
    fixAttempts: 0
  },
];

const initialAlerts: AlertData[] = [
  { 
    id: "ALT-001", 
    alertType: "high_temp",
    title: "High Temperature", 
    message: "Temperature in BOX123 has exceeded the safe threshold of 26°C. Current reading is 29°C. Turn on Peltier cooling to reduce temperature.", 
    boxId: "BOX123", 
    boxName: "Storage Box 1", 
    severity: "critical", 
    timestamp: "5 mins ago", 
    resolved: false,
    fixAttempts: 0
  },
  { 
    id: "ALT-002", 
    alertType: "low_humidity",
    title: "Low Humidity", 
    message: "Humidity levels in BOX123 are below the safe threshold. Current reading is 52%. Turn on Peltier cooling to increase humidity.", 
    boxId: "BOX123", 
    boxName: "Storage Box 1", 
    severity: "warning", 
    timestamp: "10 mins ago", 
    resolved: false,
    fixAttempts: 0
  },
  { 
    id: "ALT-003", 
    alertType: "sensor_disconnected",
    title: "Sensor Disconnected", 
    message: "Temperature sensor in BOX124 has lost connection. Please check the sensor connection and ensure the box is powered.", 
    boxId: "BOX124", 
    boxName: "Storage Box 2", 
    severity: "critical", 
    timestamp: "15 mins ago", 
    resolved: false,
    fixAttempts: 0
  },
  { 
    id: "ALT-004", 
    alertType: "box_movement",
    title: "Box Movement Detected", 
    message: "Unexpected movement detected in BOX124. Ensure the box is securely placed and not being tampered with.", 
    boxId: "BOX124", 
    boxName: "Storage Box 2", 
    severity: "warning", 
    timestamp: "20 mins ago", 
    resolved: false,
    fixAttempts: 0
  },
];

type ViewState =
  | { type: "dashboard" }
  | { type: "boxDetails"; boxId: string }
  | { type: "alertDetails"; alertId: string }
  | { type: "settings" };

export default function HomePage() {
  const { farmer, logout, updateFarmer, setLanguage } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const { toast } = useToast();
  const t = useTranslation();
  const [activeTab, setActiveTab] = useState<TabId>("home");
  const [viewState, setViewState] = useState<ViewState>({ type: "dashboard" });
  const [boxes, setBoxes] = useState(initialBoxes);
  const [alerts, setAlerts] = useState(initialAlerts);
  const [languageModalOpen, setLanguageModalOpen] = useState(false);
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);

  const activeAlerts = alerts.filter((a) => !a.resolved);
  const firstName = farmer?.fullName?.split(" ")[0] || "Farmer";

  useEffect(() => {
    boxes.forEach((box) => {
      const isSafe = box.temperature <= 26 && box.humidity >= 55 && box.humidity <= 75;

      if (box.peltierOn && box.status !== "online" && isSafe) {
        setBoxes((prev) =>
          prev.map((b) => (b.id === box.id ? { ...b, status: "online" } : b))
        );
      }
    });
  }, [boxes]);

  const handleTabChange = (tab: TabId) => {
    setActiveTab(tab);
    setViewState({ type: "dashboard" });
  };

  const handleBoxClick = (boxId: string) => {
    setViewState({ type: "boxDetails", boxId });
  };

  const handleAlertClick = (alertId: string) => {
    setViewState({ type: "alertDetails", alertId });
  };

  const handleBack = () => {
    setViewState({ type: "dashboard" });
  };

  const handleTogglePeltier = (boxId: string, on: boolean) => {
    setBoxes((prev) =>
      prev.map((b) => {
        if (b.id !== boxId) return b;

        let newTemp = b.temperature;
        let newHumidity = b.humidity;
        let newStatus: BoxStatus = b.status;

        if (on) {
          newTemp = Math.max(22, b.temperature - 4);
          newHumidity = Math.min(70, b.humidity + 10);

          if (newTemp <= 26 && newHumidity >= 55 && newHumidity <= 75) {
            newStatus = "online";
          }
        } else {
          newTemp = Math.min(32, b.temperature + 3);
          newHumidity = Math.max(45, b.humidity - 8);

          if (newTemp > 26 || newHumidity < 55 || newHumidity > 75) {
            newStatus = "warning";
          }
        }

        return { 
          ...b, 
          peltierOn: on, 
          temperature: newTemp, 
          humidity: newHumidity,
          status: newStatus
        };
      })
    );

    toast({
      title: on ? t("peltierActivated") : t("peltierDeactivated"),
      description: on ? t("coolingSysOn") : t("coolingSysOff"),
    });
  };

  const handleResolveAlert = (alertId: string) => {
    const alert = alerts.find((a) => a.id === alertId);
    if (!alert) return;

    const box = boxes.find((b) => b.id === alert.boxId);
    if (!box) return;

    const isSafe = box.temperature <= 26 && box.humidity >= 55 && box.humidity <= 75;

    if (isSafe && alert.fixAttempts === 0) {
      setAlerts((prev) =>
        prev.map((a) => (a.id === alertId ? { ...a, resolved: true, fixAttempts: 1 } : a))
      );
      toast({
        title: t("alertResolved") + " ✓",
        description: t("alertMarkedResolved"),
      });
    } else if (!isSafe && alert.fixAttempts === 0) {
      setAlerts((prev) =>
        prev.map((a) => (a.id === alertId ? { ...a, fixAttempts: 1 } : a))
      );
      toast({
        title: t("takeAction"),
        description: "Conditions are not yet safe. Please adjust Peltier settings.",
        variant: "destructive",
      });
    } else if (!isSafe && alert.fixAttempts === 1) {
      setAlerts((prev) =>
        prev.map((a) => (a.id === alertId ? { ...a, fixAttempts: 2 } : a))
      );
      toast({
        title: t("boxRecall"),
        description: "Multiple fix attempts failed. Consider recalling the box.",
        variant: "destructive",
      });
    } else if (isSafe) {
      setAlerts((prev) =>
        prev.map((a) => (a.id === alertId ? { ...a, resolved: true } : a))
      );
      toast({
        title: t("alertResolved") + " ✓",
        description: t("alertMarkedResolved"),
      });
    }

    setViewState({ type: "dashboard" });
  };

  const handleLanguageChange = (language: string) => {
    const langCode = language === "English" ? "EN" : 
                     language === "Hindi" ? "HI" : 
                     language === "Marathi" ? "MR" : 
                     language === "Gujarati" ? "GU" : "EN";
    setLanguage(langCode as any);
    updateFarmer({ language: langCode as any });
    setLanguageModalOpen(false);
    toast({
      title: "Language Updated",
      description: `Language changed to ${language}`,
    });
  };

  const handleLogout = () => {
    logout();
    toast({
      title: "Logged Out",
      description: "You have been logged out successfully.",
    });
  };

  const getLanguageName = () => {
    switch(farmer?.language) {
      case "HI": return "Hindi";
      case "MR": return "Marathi";
      case "GU": return "Gujarati";
      default: return "English";
    }
  };

  const renderContent = () => {
    if (viewState.type === "boxDetails") {
      const box = boxes.find((b) => b.id === viewState.boxId);
      if (!box) return null;
      return (
        <BoxDetails
          {...box}
          onBack={handleBack}
          onTogglePeltier={(on) => handleTogglePeltier(box.id, on)}
        />
      );
    }

    if (viewState.type === "alertDetails") {
      const alert = alerts.find((a) => a.id === viewState.alertId);
      if (!alert) return null;
      return (
        <AlertDetails
          {...alert}
          onBack={handleBack}
          onResolve={() => handleResolveAlert(alert.id)}
        />
      );
    }

    if (viewState.type === "settings") {
      return (
        <SettingsScreen
          darkMode={theme === "dark"}
          notificationsEnabled={notificationsEnabled}
          language={getLanguageName()}
          onBack={handleBack}
          onToggleDarkMode={toggleTheme}
          onToggleNotifications={() => setNotificationsEnabled(!notificationsEnabled)}
          onChangeLanguage={() => setLanguageModalOpen(true)}
          onHelpSupport={() => toast({ title: "Help & Support", description: "Opening help center..." })}
          onPrivacy={() => toast({ title: "Privacy Policy", description: "Opening privacy policy..." })}
        />
      );
    }

    switch (activeTab) {
      case "home":
        return (
          <motion.div
            className="pb-24"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.3 }}
          >
            <DashboardHeader
              farmerName={firstName}
              notificationCount={activeAlerts.length}
              onNotificationsClick={() => setActiveTab("alerts")}
              onLanguageClick={() => setLanguageModalOpen(true)}
            />
            <div className="px-5 space-y-4">
              <SummaryCard
                type="boxes"
                value={boxes.length}
                label={t("myBoxes")}
                onClick={() => setActiveTab("boxes")}
              />
              <SummaryCard
                type="alerts"
                value={activeAlerts.length}
                secondaryValue={t("alertsActive").toLowerCase()}
                label={t("activeAlerts")}
                onClick={() => setActiveTab("alerts")}
              />
              <SummaryCard
                type="summary"
                value={activeAlerts.length === 0 ? t("allSystemsNormal") : t("checkRequired")}
                label={t("todaysSummary")}
              />

              <Card className="p-5 rounded-3xl">
                <div className="flex items-start gap-4">
                  <div className="p-3 rounded-2xl bg-primary/10">
                    <div className="relative">
                      <Shield size={28} className="text-primary" />
                      <Leaf size={14} className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 text-primary" />
                    </div>
                  </div>
                  <div className="flex-1">
                    <h3 className="font-semibold text-foreground flex items-center gap-2">
                      {t("herbtraceInfo")}
                      <Info size={14} className="text-muted-foreground" />
                    </h3>
                    <p className="text-sm text-muted-foreground mt-1 leading-relaxed">
                      {t("iotMonitoring")}
                    </p>
                  </div>
                </div>
              </Card>

              <div className="pt-4">
                <h2 className="text-lg font-semibold text-foreground mb-3">{t("myBoxes")}</h2>
                <div className="space-y-3">
                  {boxes.map((box) => (
                    <BoxCard
                      key={box.id}
                      {...box}
                      onClick={() => handleBoxClick(box.id)}
                    />
                  ))}
                </div>
              </div>
            </div>
          </motion.div>
        );

      case "boxes":
        return (
          <motion.div
            className="pb-24"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.3 }}
          >
            <header className="px-5 py-4">
              <h1 className="text-2xl font-bold text-foreground">{t("myBoxes")}</h1>
              <p className="text-muted-foreground">{boxes.length} {t("boxesRegistered")}</p>
            </header>
            <div className="px-5 space-y-3">
              {boxes.map((box) => (
                <BoxCard
                  key={box.id}
                  {...box}
                  onClick={() => handleBoxClick(box.id)}
                />
              ))}
            </div>
          </motion.div>
        );

      case "alerts":
        return (
          <motion.div
            className="pb-24"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.3 }}
          >
            <header className="px-5 py-4">
              <h1 className="text-2xl font-bold text-foreground">{t("alerts")}</h1>
              <p className="text-muted-foreground">{activeAlerts.length} {t("alertsActive")}</p>
            </header>
            <div className="px-5 space-y-3">
              {alerts.map((alert) => (
                <AlertCard
                  key={alert.id}
                  {...alert}
                  onClick={() => handleAlertClick(alert.id)}
                />
              ))}
            </div>
          </motion.div>
        );

      case "profile":
        return (
          <ProfileScreen
            name={farmer?.fullName || ""}
            mobile={farmer?.mobile || ""}
            email={farmer?.email || ""}
            state={farmer?.state || ""}
            city={farmer?.city || ""}
            areaVillage={farmer?.areaVillage || ""}
            language={getLanguageName()}
            onEditProfile={() => toast({ title: "Edit Profile", description: "Opening profile editor..." })}
            onSettings={() => setViewState({ type: "settings" })}
            onLogout={handleLogout}
          />
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <AnimatePresence mode="wait">{renderContent()}</AnimatePresence>

      {viewState.type === "dashboard" && (
        <BottomNavigation
          activeTab={activeTab}
          onTabChange={handleTabChange}
          alertCount={activeAlerts.length}
        />
      )}

      <LanguageModal
        isOpen={languageModalOpen}
        currentLanguage={getLanguageName()}
        onClose={() => setLanguageModalOpen(false)}
        onSelect={handleLanguageChange}
      />
    </div>
  );
}

client/src/components/ui/toggle-group.ts.content:
"use client"

import * as React from "react"
import * as ToggleGroupPrimitive from "@radix-ui/react-toggle-group"
import { type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import { toggleVariants } from "@/components/ui/toggle"

const ToggleGroupContext = React.createContext<
  VariantProps<typeof toggleVariants>
>({
  size: "default",
  variant: "default",
})

const ToggleGroup = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, children, ...props }, ref) => (
  <ToggleGroupPrimitive.Root
    ref={ref}
    className={cn("flex items-center justify-center gap-1", className)}
    {...props}
  >
    <ToggleGroupContext.Provider value={{ variant, size }}>
      {children}
    </ToggleGroupContext.Provider>
  </ToggleGroupPrimitive.Root>
))

ToggleGroup.displayName = ToggleGroupPrimitive.Root.displayName

const ToggleGroupItem = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Item> &
    VariantProps<typeof toggleVariants>
>(({ className, children, variant, size, ...props }, ref) => {
  const context = React.useContext(ToggleGroupContext)

  return (
    <ToggleGroupPrimitive.Item
      ref={ref}
      className={cn(
        toggleVariants({
          variant: context.variant || variant,
          size: context.size || size,
        }),
        className
      )}
      {...props}
    >
      {children}
    </ToggleGroupPrimitive.Item>
  )
})

ToggleGroupItem.displayName = ToggleGroupPrimitive.Item.displayName

export { ToggleGroup, ToggleGroupItem }

client/src/components/BottomNavigation.t.content:
import { motion } from "framer-motion";
import { Home, Box, Bell, User } from "lucide-react";
import { useTranslation } from "@/hooks/useTranslation";

type TabId = "home" | "boxes" | "alerts" | "profile";

interface BottomNavigationProps {
  activeTab: TabId;
  onTabChange: (tab: TabId) => void;
  alertCount?: number;
}

export default function BottomNavigation({
  activeTab,
  onTabChange,
  alertCount = 0,
}: BottomNavigationProps) {
  const t = useTranslation();

  const tabs: { id: TabId; labelKey: string; icon: typeof Home }[] = [
    { id: "home", labelKey: "home", icon: Home },
    { id: "boxes", labelKey: "boxes", icon: Box },
    { id: "alerts", labelKey: "alerts", icon: Bell },
    { id: "profile", labelKey: "profile", icon: User },
  ];

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-card border-t border-card-border safe-area-pb z-50">
      <div className="flex items-center justify-around h-16 max-w-lg mx-auto">
        {tabs.map((tab) => {
          const isActive = activeTab === tab.id;
          const Icon = tab.icon;

          return (
            <motion.button
              key={tab.id}
              onClick={() => onTabChange(tab.id)}
              className={`flex flex-col items-center justify-center flex-1 h-full relative ${
                isActive ? "text-primary" : "text-muted-foreground"
              }`}
              whileTap={{ scale: 0.92 }}
              transition={{ duration: 0.15 }}
              data-testid={`nav-${tab.id}`}
            >
              <div className="relative">
                <Icon size={24} strokeWidth={isActive ? 2.5 : 2} />
                {tab.id === "alerts" && alertCount > 0 && (
                  <span className="absolute -top-1 -right-1 min-w-[18px] h-[18px] flex items-center justify-center text-[10px] font-bold text-white bg-destructive rounded-full px-1">
                    {alertCount > 9 ? "9+" : alertCount}
                  </span>
                )}
              </div>
              <span
                className={`text-[11px] mt-1 ${
                  isActive ? "font-semibold" : "font-medium"
                }`}
              >
                {t(tab.labelKey)}
              </span>
              {isActive && (
                <motion.div
                  className="absolute top-0 left-1/2 -translate-x-1/2 w-8 h-0.5 bg-primary rounded-full"
                  layoutId="activeTab"
                  transition={{ type: "spring", stiffness: 400, damping: 30 }}
                />
              )}
            </motion.button>
          );
        })}
      </div>
    </nav>
  );
}

export type { TabId };

