import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertSampleSchema, type Sample } from "@shared/schema";
import { useUpdateSample } from "@/hooks/use-samples";
import { useLanguage } from "@/lib/i18n";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";

interface EditSampleDialogProps {
  sample: Sample | null;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function EditSampleDialog({ sample, open, onOpenChange }: EditSampleDialogProps) {
  const { t } = useLanguage();
  const updateSample = useUpdateSample();

  const form = useForm({
    resolver: zodResolver(insertSampleSchema.partial()),
    defaultValues: {
      status: "pending",
      temperature: "",
      humidity: "",
    },
  });

  // Reset form when sample changes
  useEffect(() => {
    if (sample) {
      form.reset({
        status: sample.status,
        temperature: sample.temperature || "",
        humidity: sample.humidity || "",
      });
    }
  }, [sample, form]);

  const onSubmit = (data: any) => {
    if (!sample) return;
    updateSample.mutate(
      { id: sample.id, ...data },
      {
        onSuccess: () => onOpenChange(false),
      }
    );
  };

  if (!sample) return null;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-lg rounded-2xl">
        <DialogHeader>
          <DialogTitle className="font-display text-xl">{sample.herbName} - {sample.batchId}</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="status"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t("samples.status")}</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select status" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="pending">{t("status.pending")}</SelectItem>
                      <SelectItem value="in_testing">{t("status.in_testing")}</SelectItem>
                      <SelectItem value="completed">{t("status.completed")}</SelectItem>
                      <SelectItem value="rejected">{t("status.rejected")}</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="space-y-4">
              <FormField
                control={form.control}
                name="temperature"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Temperature</FormLabel>
                    <FormControl>
                      <Input placeholder="24Â°C" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="humidity"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Humidity</FormLabel>
                    <FormControl>
                      <Input placeholder="45%" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="flex justify-end pt-4">
              <Button 
                type="submit" 
                className="w-full sm:w-auto bg-primary hover:bg-primary/90 rounded-xl"
                disabled={updateSample.isPending}
                data-testid="button-update-sample"
              >
                {updateSample.isPending ? "Updating..." : "Update Sample"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

server/static.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";

export function serveStatic(app: Express) {
  const distPath = path.resolve(__dirname, "public");
  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

