import { db } from "./db";
import {
  users, samples, recalls, notifications,
  type User, type InsertUser,
  type Sample, type InsertSample, type UpdateSampleRequest,
  type Recall,
  type Notification
} from "@shared/schema";
import { eq, desc } from "drizzle-orm";

export interface IStorage {
  // Users / Auth
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, updates: Partial<InsertUser>): Promise<User>;

  // Samples
  getSamples(): Promise<Sample[]>;
  getSample(id: number): Promise<Sample | undefined>;
  createSample(sample: InsertSample): Promise<Sample>;
  updateSample(id: number, updates: UpdateSampleRequest): Promise<Sample>;

  // Recalls
  getRecalls(): Promise<Recall[]>;

  // Notifications
  getNotifications(userId: number): Promise<Notification[]>;
  markNotificationRead(id: number): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: number, updates: Partial<InsertUser>): Promise<User> {
    const [user] = await db.update(users).set(updates).where(eq(users.id, id)).returning();
    return user;
  }

  async getSamples(): Promise<Sample[]> {
    return await db.select().from(samples).orderBy(desc(samples.assignedDate));
  }

  async getSample(id: number): Promise<Sample | undefined> {
    const [sample] = await db.select().from(samples).where(eq(samples.id, id));
    return sample;
  }

  async createSample(insertSample: InsertSample): Promise<Sample> {
    const [sample] = await db.insert(samples).values(insertSample).returning();
    return sample;
  }

  async updateSample(id: number, updates: UpdateSampleRequest): Promise<Sample> {
    const [sample] = await db
      .update(samples)
      .set(updates)
      .where(eq(samples.id, id))
      .returning();
    return sample;
  }

  async getRecalls(): Promise<Recall[]> {
    return await db.select().from(recalls).orderBy(desc(recalls.date));
  }

  async getNotifications(userId: number): Promise<Notification[]> {
    return await db.select()
      .from(notifications)
      .where(eq(notifications.userId, userId))
      .orderBy(desc(notifications.timestamp));
  }

  async markNotificationRead(id: number): Promise<void> {
    await db.update(notifications).set({ read: true }).where(eq(notifications.id, id));
  }
}

export const storage = new DatabaseStorage();

