import { useSamples } from "@/hooks/use-samples";
import { useLanguage } from "@/lib/i18n";
import { Navbar } from "@/components/layout/Navbar";
import { motion } from "framer-motion";
import { FileText, AlertCircle, CheckCircle2, Activity } from "lucide-react";
import { format } from "date-fns";
import { SampleCard } from "@/components/samples/SampleCard";
import { useState } from "react";
import { EditSampleDialog } from "@/components/samples/EditSampleDialog";
import { type Sample } from "@shared/schema";

export default function Dashboard() {
  const { data: samples, isLoading } = useSamples();
  const { t } = useLanguage();
  const [selectedSample, setSelectedSample] = useState<Sample | null>(null);
  const [editOpen, setEditOpen] = useState(false);

  const handleSampleClick = (sample: Sample) => {
    setSelectedSample(sample);
    setEditOpen(true);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-[#F8FAF9] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  const pendingCount = samples?.filter(s => s.status === "pending").length || 0;
  const testingCount = samples?.filter(s => s.status === "in_testing").length || 0;
  const completedCount = samples?.filter(s => s.status === "completed").length || 0;
  const total = samples?.length || 1;
  const accuracy = Math.round((completedCount / total) * 100);

  // Recent activity (mock derived from samples)
  const recentSamples = [...(samples || [])].sort((a, b) => 
    new Date(b.assignedDate || 0).getTime() - new Date(a.assignedDate || 0).getTime()
  ).slice(0, 3);

  return (
    <div className="min-h-screen bg-[#F8FAF9] pb-20 md:pb-0 md:pl-64">
      <Navbar />

      <main className="p-4 md:p-8 max-w-7xl mx-auto space-y-8">

        {/* Offline Banner */}
        {!navigator.onLine && (
          <motion.div 
            initial={{ height: 0 }} 
            animate={{ height: "auto" }}
            className="bg-amber-100 text-amber-800 px-4 py-2 rounded-lg text-sm font-medium text-center border border-amber-200"
          >
            {t("common.offline")}
          </motion.div>
        )}

        {/* Welcome Section */}
        <div className="mb-6">
          <h1 className="text-3xl md:text-4xl font-display font-bold text-foreground">
            {t("dashboard.title")}
          </h1>
          <p className="text-muted-foreground mt-1">Overview of your lab activity</p>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Card 1: Assigned */}
          <motion.div 
            whileHover={{ y: -4 }}
            className="bg-white rounded-3xl p-6 shadow-sm border border-slate-100 flex flex-col justify-between h-40"
          >
            <div>
              <p className="text-muted-foreground font-medium">{t("dashboard.assigned")}</p>
              <h2 className="text-5xl font-display font-bold text-primary mt-2">{pendingCount + testingCount}</h2>
            </div>
            <div className="flex items-center gap-2 text-sm text-primary font-medium">
              <span className="bg-primary/10 px-2 py-1 rounded-full">+{samples?.filter(s => s.status === 'pending').length} new</span>
            </div>
          </motion.div>

          {/* Card 2: Attention */}
          <motion.div 
            whileHover={{ y: -4 }}
            className="bg-amber-50 rounded-3xl p-6 shadow-sm border border-amber-100 flex flex-col justify-between h-40"
          >
            <div>
              <p className="text-amber-800 font-medium">{t("dashboard.attention")}</p>
              <h2 className="text-5xl font-display font-bold text-amber-600 mt-2">{pendingCount}</h2>
            </div>
            <div className="flex items-center gap-2 text-sm text-amber-700 font-medium">
              <span>Samples pending testing</span>
            </div>
          </motion.div>

          {/* Card 3: Accuracy */}
          <motion.div 
            whileHover={{ y: -4 }}
            className="bg-white rounded-3xl p-6 shadow-sm border border-slate-100 flex flex-col justify-between h-40"
          >
            <div>
              <p className="text-muted-foreground font-medium">{t("dashboard.accuracy")}</p>
              <h2 className="text-5xl font-display font-bold text-slate-800 mt-2">{accuracy}%</h2>
            </div>
            <div className="w-full bg-slate-100 h-2 rounded-full mt-2 overflow-hidden">
              <div className="bg-green-500 h-full rounded-full" style={{ width: `${accuracy}%` }}></div>
            </div>
          </motion.div>
        </div>

        {/* Recent Activity Section */}
        <div>
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-display font-bold flex items-center gap-2">
              <Activity className="w-5 h-5 text-primary" />
              {t("dashboard.activity")}
            </h2>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
            {recentSamples.map((sample) => (
              <SampleCard 
                key={sample.id} 
                sample={sample} 
                onClick={() => handleSampleClick(sample)} 
              />
            ))}
          </div>
        </div>
      </main>

      <EditSampleDialog 
        sample={selectedSample} 
        open={editOpen} 
        onOpenChange={setEditOpen} 
      />
    </div>
  );
}

