import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/hooks/use-auth";
import { useLanguage } from "@/lib/i18n";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { motion } from "framer-motion";

const profileSetupSchema = z.object({
  name: z.string().min(2, "Full name required"),
  language: z.enum(["en", "hi", "mr", "gu"]),
  theme: z.enum(["light", "dark"]),
});

type ProfileSetupData = z.infer<typeof profileSetupSchema>;

export default function ProfileSetup() {
  const { user } = useAuth();
  const { t, setLanguage, language } = useLanguage();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<ProfileSetupData>({
    resolver: zodResolver(profileSetupSchema),
    defaultValues: {
      name: user?.name || "",
      language: (language as any) || "en",
      theme: "light",
    },
  });

  const onSubmit = async (data: ProfileSetupData) => {
    setIsSubmitting(true);
    try {
      setLanguage(data.language as any);
      if (data.theme === "dark") {
        document.documentElement.classList.add("dark");
      } else {
        document.documentElement.classList.remove("dark");
      }
      localStorage.setItem("herbtrace-theme", data.theme);

      // Call API to complete profile
      const res = await fetch("/api/auth/complete-profile", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: data.name }),
        credentials: "include",
      });

      if (!res.ok) throw new Error("Failed to complete profile");

      // Refresh user data
      window.location.href = "/";
    } catch (error) {
      console.error("Profile setup error:", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!user) return null;

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-[#F8FAF9] p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md"
      >
        <div className="text-center mb-8">
          <h1 className="text-3xl font-display font-bold text-foreground">HerbTrace</h1>
          <p className="text-muted-foreground mt-2 font-medium">Complete Your Profile</p>
        </div>

        <Card className="border-0 shadow-2xl shadow-black/5 rounded-3xl overflow-hidden bg-white/80 backdrop-blur-sm">
          <div className="p-8">
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="name" className="text-muted-foreground">
                  Full Name
                </Label>
                <Input
                  id="name"
                  className="h-12 rounded-xl bg-slate-50 border-slate-200 focus:bg-white transition-all"
                  placeholder="Your name"
                  data-testid="input-fullname"
                  {...form.register("name")}
                />
                {form.formState.errors.name && (
                  <p className="text-sm text-red-500">{form.formState.errors.name.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="language" className="text-muted-foreground">
                  Preferred Language
                </Label>
                <Select
                  value={form.watch("language")}
                  onValueChange={(value) => form.setValue("language", value as any)}
                >
                  <SelectTrigger className="h-12 rounded-xl bg-slate-50 border-slate-200 focus:bg-white" data-testid="select-language">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="en">English</SelectItem>
                    <SelectItem value="hi">à¤¹à¤¿à¤à¤¦à¥</SelectItem>
                    <SelectItem value="mr">à¤®à¤°à¤¾à¤ à¥</SelectItem>
                    <SelectItem value="gu">àªà«àªàª°àª¾àª¤à«</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="theme" className="text-muted-foreground">
                  Theme
                </Label>
                <Select
                  value={form.watch("theme")}
                  onValueChange={(value) => form.setValue("theme", value as any)}
                >
                  <SelectTrigger className="h-12 rounded-xl bg-slate-50 border-slate-200 focus:bg-white" data-testid="select-theme">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="light">Light</SelectItem>
                    <SelectItem value="dark">Dark</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <Button
                type="submit"
                className="w-full h-12 text-lg font-semibold rounded-xl bg-primary hover:bg-primary/90 shadow-lg shadow-primary/25 hover:shadow-primary/40 transition-all"
                disabled={isSubmitting}
                data-testid="button-save-profile"
              >
                {isSubmitting ? "Saving..." : "Save & Continue"}
              </Button>
            </form>
          </div>
        </Card>
      </motion.div>
    </div>
  );
}

